import{_ as A,c as i,b as e,a as o,d as p,e as a,w as s,r as d,o as l}from"./app-CRAEKUA-.js";const c="/assets/erd-school-Bn2oFNGW.svg",g="/assets/erd-example-B-nE5qw8.svg",R="/assets/sampleerdiagram-DXDpLwGD.svg",E="data:image/png;base64,",m="data:image/png;base64,",h="data:image/png;base64,",b="data:image/png;base64,",k="data:image/png;base64,",S="data:image/png;base64,",y="data:image/png;base64,",q="data:image/png;base64,",u="/assets/libraryerdiagram-DVYV5zm7.svg",K="data:image/png;base64,",Q="/assets/Keys-DPQVgWPh.png",C="data:image/png;base64,",J="/assets/ERD-Notation-D0-eVPC0.png",M={},V={class:"hint-container warning"};function T(U,t){const r=d("RouteLink"),n=d("Mermaid");return l(),i("div",null,[e("div",V,[t[5]||(t[5]=e("p",{class:"hint-container-title"},"Примечание",-1)),t[6]||(t[6]=e("p",null,[e("strong",null,"ЭТО АРХИВНАЯ ВЕРСИЯ КУРСА!")],-1)),e("p",null,[t[1]||(t[1]=a('Материалы предназначаются для пересдающих дисциплину "ОП.08 - Основы проектирования баз данных" в соответствии с учебными планами СПО годов набора ',-1)),t[2]||(t[2]=e("strong",null,"ДО 2024-го",-1)),t[3]||(t[3]=a('. Для того чтобы ознакомиться с актуальным курсом "ИТ.03 - Основы проектирования баз данных" включающим интерактивные примеры, ',-1)),p(r,{to:"/disciplines/it03/"},{default:s(()=>[...t[0]||(t[0]=[a("перейдите по ссылке",-1)])]),_:1}),t[4]||(t[4]=a(".",-1))]),t[7]||(t[7]=e("p",null,"Материалы были перенесены со старого сайта с минимальной доработкой, поэтому не все возможности курса могут работать как ожидается, где-то может слететь форматирование.",-1)),t[8]||(t[8]=e("p",null,[e("strong",null,"Домашние задания в рамках курса проверяться не будут!")],-1))]),t[9]||(t[9]=o('<h1 id="оп-08-10-модели-бд-и-способы-их-представления-er-диаграммы" tabindex="-1"><a class="header-anchor" href="#оп-08-10-модели-бд-и-способы-их-представления-er-диаграммы"><span>ОП.08 - 10 - Модели БД и способы их представления. ER-диаграммы</span></a></h1><h2 id="понятие-модели-бд" tabindex="-1"><a class="header-anchor" href="#понятие-модели-бд"><span>Понятие модели БД</span></a></h2><p><strong>Модель базы данных</strong> — это структура, которая определяет, как данные будут храниться, организовываться и обрабатываться в базе данных. Она состоит из таблиц, полей, связей между таблицами и ограничений целостности данных.</p><p>Мы знаем, что MySQL — это система управления реляционными базами данных (СУБД), которая использует модель БД реляционной системы. В MySQL модель БД представлена в виде набора таблиц, каждая из которых имеет свое имя и набор столбцов с определенными типами данных.</p><p>Модели БД в MySQL нужны для:</p><ol><li><p>Определения структуры базы данных: модели помогают определить таблицы, поля, типы данных, индексы и другие параметры, необходимые для создания базы данных.</p></li><li><p>Управления данными: модели позволяют установить связи между таблицами, что обеспечивает целостность данных и предотвращает ошибки при вводе и обработке информации.</p></li><li><p>Оптимизации производительности: модели помогают оптимизировать запросы к базе данных, что повышает скорость выполнения операций и улучшает производительность.</p></li><li><p>Разработки приложений: модели БД помогают разработчикам создавать приложения, которые могут работать с базой данных, используя стандартные языки запросов (SQL).</p></li></ol><p>Таким образом, модели БД в MySQL играют важную роль в проектировании, управлении и оптимизации баз данных, а также в разработке приложений, которые используют эти данные.</p><p>Но при этом стоит помнить, что модель базы данных — это не сама база данных, а лишь её проект. Модели можно описывать различными способами и используя различные инструменты, но основным способом их описания является <em>ER-модель</em> выраженная через <em>ER-диаграмму</em>.</p><h2 id="что-такое-er-модель-и-er-диаграмма" tabindex="-1"><a class="header-anchor" href="#что-такое-er-модель-и-er-диаграмма"><span>Что такое ER-модель и ER-диаграмма?</span></a></h2><p><strong>ER-модель</strong> (от англ. Entity-Relationship model, модель «сущность — связь») — модель данных, позволяющая описывать концептуальные схемы предметной области.</p><p>ER-модель представляет собой формальную конструкцию, которая сама по себе не предписывает никаких графических средств её визуализации. В качестве стандартной графической нотации, с помощью которой можно визуализировать ER-модель, была предложена диаграмма «сущность-связь» (англ. Entity-Relationship diagram, ERD, ER-диаграмма).</p><figure><img src="'+c+'" alt="Пример ERD - Попроще | block" tabindex="0" loading="lazy"><figcaption>Пример ERD - Попроще | block</figcaption></figure><p><strong>ER-диаграмма</strong> — это разновидность блок-схемы, где показано, как разные «сущности» (люди, объекты, концепции и так далее) связаны между собой внутри системы. ER-диаграммы чаще всего применяются для проектирования и отладки реляционных баз данных и информационных систем для бизнеса. ER-диаграммы (или ER-модели) полагаются на стандартный набор символов, включая прямоугольники, ромбы, овалы и соединительные линии, для отображения сущностей, их атрибутов и связей. Эти диаграммы устроены по тому же принципу, что и грамматические структуры: сущности выполняют роль существительных, а связи — глаголов.</p><p>Понятия «ER-модель» и «ER-диаграмма» часто не различают, хотя для визуализации ER-моделей могут быть использованы и другие графические нотации, либо визуализация может вообще не применяться (например, использоваться текстовое описание).</p><h3 id="уровни-детализации" tabindex="-1"><a class="header-anchor" href="#уровни-детализации"><span>Уровни детализации</span></a></h3><p>В ER-моделях и моделях данных обычно выделяют до трех уровней детализации:</p><p><strong>Концептуальная модель данных</strong> — схема наивысшего уровня с минимальным количеством подробностей. Достоинство этого подхода заключается в возможности отобразить общую структуру модели и всю архитектуру системы. Менее масштабные системы могут обойтись и без этой модели. В этом случае можно сразу переходить к логической модели.</p><p><strong>Логическая модель данных</strong> — содержит более подробную информацию, нежели концептуальная модель. На этом уровне определяются более подробные операционные и транзакционные сущности. Логическая модель не зависит от технологии, в которой она будет применяться.</p><p><strong>Физическая модель данных</strong> — на основе каждой логической модели данных можно составить одну или две физических модели. В последних должно присутствовать достаточно технических подробностей для составления и внедрения самой базы данных.</p><p>Похожие уровни масштаба и детализации встречаются и в других видах схем (например, в диаграммах DFD), однако данная классификация отличается от подхода в разработке ПО, где деление информации осуществляется по несколько иному принципу. Правда, иногда разработчики применяют ER-диаграммы с дополнительными иерархиями, если дизайн базы данных требует больше информационных уровней. К примеру, разработчик может добавить новые группы по принципу расширения вверх (суперклассы) и вниз (подклассы).</p><h3 id="формальные-требования-при-составлении-erd" tabindex="-1"><a class="header-anchor" href="#формальные-требования-при-составлении-erd"><span>Формальные требования при составлении ERD</span></a></h3><p>При составлении ERD следует учитывать некоторые формальные требования:</p><p><strong>Только реляционные данные</strong> — Следует четко понимать, что цель ER-диаграмм — показать связи и отношения между элементами, поэтому они отображают только реляционную структуру.</p><p><strong>Только для структурированных данных</strong> — Данные должны быть четко разбиты на поля, столбцы и строки, иначе пользы от ER-диаграммы будет мало. Это касается и частично структурированных данных, так как только некоторые из них будут пригодны для работы.</p><p><strong>Сложность интеграции с существующей базой данных</strong> — Применение ER-моделей для интеграции с существующей базой данных — непростая задача по причине различия в архитектурах.</p><figure><img src="'+g+'" alt="Пример ERD - Посложнее | block" tabindex="0" loading="lazy"><figcaption>Пример ERD - Посложнее | block</figcaption></figure><h3 id="области-применения-диаграмм-«сущность-связь»" tabindex="-1"><a class="header-anchor" href="#области-применения-диаграмм-«сущность-связь»"><span>Области применения диаграмм «сущность-связь»</span></a></h3><ul><li><p><strong>Проектирование баз данных</strong></p><p>ER-диаграммы применяются для моделирования и проектирования реляционных баз данных, причем как в плане логических и бизнес-правил (логические модели данных), так и в плане внедрения конкретных технологий (физические модели данных). В сфере разработки программного обеспечения ER-диаграмма, как правило, служит первым шагом в определении требований проекта по созданию информационных систем. На дальнейших этапах работы ER-диаграммы также применяются для моделирования конкретных баз данных. Реляционная база данных сопровождается соответствующей реляционной таблицей и при необходимости может быть представлена в этом формате.</p></li><li><p><strong>Отладка баз данных</strong></p><p>ER-диаграммы применяются для анализа уже имеющихся баз данных с целью выявить и устранить ошибки в логике или развертывании. Диаграмма позволяет выявить, где именно закрались ошибки.</p></li><li><p><strong>Информационные системы для бизнеса</strong></p><p>ER-схемы используются для проектирования и анализа реляционных баз данных, применяемых в бизнес-процессах. Реляционные базы данных могут пригодиться в любом бизнес-процессе, где задействованы данные, разбитые на поля, включая сущности, действия и взаимосвязи. Базы данных помогают оптимизировать процессы, извлекать данные и повышать качество результатов.</p></li><li><p><strong>Реорганизация бизнес-процессов (BPR)</strong></p><p>ER-диаграммы помогают анализировать базы данных, применяемые при реорганизации бизнес-процессов и моделировании новых баз данных.</p></li><li><p><strong>Образование</strong></p><p>Базы данных — широко распространенный в наши дни способ хранения реляционной информации, применяемой в целях образования и для последующего извлечения данных, поэтому ER-диаграммы играют не последнюю роль в планировании подобных структур данных.</p></li><li><p><strong>Исследовательская деятельность</strong></p><p>Поскольку исследовательская работа во многом опирается на четко структурированные данные, ER-диаграммы играют ключевую роль в построении оптимальных баз данных для анализа информации.</p></li></ul><h2 id="символы-и-способы-нотации-erd" tabindex="-1"><a class="header-anchor" href="#символы-и-способы-нотации-erd"><span>Символы и способы нотации ERD</span></a></h2><p>Диаграммы «сущность-связь» (или ERD) — неотъемлемая составляющая процесса моделирования любых систем, включая простые и сложные базы данных, однако применяемые в них фигуры и способы нотации могут ввести в заблуждение. Концептуальные модели данных дают общее представление о том, что должно входить в состав модели. Концептуальные ER-диаграммы можно брать за основу логических моделей данных. Их также можно использовать для создания отношений общности между разными ER-моделями, положив их в основу интеграции.</p><h3 id="концептуальная-erd" tabindex="-1"><a class="header-anchor" href="#концептуальная-erd"><span>Концептуальная ERD</span></a></h3><p><img src="'+R+'" alt="Шаблон ER-диаграммы заказа на покупку | block" loading="lazy"><br><em>Шаблон ER-диаграммы заказа на покупку</em></p><p>На этой ER-диаграмме показано, какие сущности, связи и атрибуты задействованы в документе заказа на покупку. Схема позволяет наглядно представить задействованную информацию, чтобы ее было проще понять и объяснить.</p><h4 id="символы-erd-сущностеи" tabindex="-1"><a class="header-anchor" href="#символы-erd-сущностеи"><span>Символы ERD-сущностей</span></a></h4><p>Под понятием «сущности» подразумеваются объекты или понятия, несущие важную информацию. С точки зрения грамматики, они, как правило, обозначаются существительными, например, «товар», «клиент», «заведение» или «промоакция». Ниже представлены три наиболее распространенных типа сущностей, используемых в ER-диаграммах.</p><p><img src="'+E+'" alt="Независимая (сильная) сущность | block" loading="lazy"><br><em>Независимая (сильная) сущность</em></p><p>Не зависит от других сущностей и часто называется «родительской», так как у нее в подчинении обычно находятся слабые сущности. Независимые сущности сопровождаются первичным ключом, который позволяет идентифицировать каждый экземпляр сущности.</p><p><img src="'+m+'" alt="Зависимая (слабая) сущность | block" loading="lazy"><br><em>Зависимая (слабая) сущность</em></p><p>Сущность, которая зависит от сущности другого типа. Не сопровождается первичным ключом и не имеет значения в схеме без своей родительской сущности.</p><p><img src="'+h+'" alt="Ассоциативная сущность | block" loading="lazy"><br><em>Ассоциативная сущность</em></p><p>Соединяет экземпляры сущностей разных типов. Также содержит атрибуты, характерные для связей между этими сущностями.</p><h4 id="символы-erd-связеи" tabindex="-1"><a class="header-anchor" href="#символы-erd-связеи"><span>Символы ERD-связей</span></a></h4><p>Связи используются в схемах «сущность-связь» для обозначения взаимодействия между двумя сущностями. Грамматически связи, как правило, выражаются глаголами, например, «назначить», «закрепить», «отследить», и несут полезную информацию, которую невозможно получить, опираясь только на типы сущностей.</p><p><img src="'+b+'" alt="Связь | block" loading="lazy"><br><em>Связь</em></p><p>Отношение между сущностями.</p><p><img src="'+k+'" alt="Слабая связь | block" loading="lazy"><br><em>Слабая связь</em></p><p>Связь между зависимой сущностью и ее «хозяином».</p><h4 id="символы-erd-атрибутов" tabindex="-1"><a class="header-anchor" href="#символы-erd-атрибутов"><span>Символы ERD-атрибутов</span></a></h4><p>ERD-атрибуты характеризуют сущности, позволяя пользователям лучше разобраться в устройстве базы данных. Атрибуты содержат информацию о сущностях, выделенных в концептуальной ER-диаграмме.</p><p><img src="'+S+'" alt="Атрибут | block" loading="lazy"><br><em>Атрибут</em></p><p>Характеризует сущность, а также отношения между двумя или более элементами.</p><p><img src="'+y+'" alt="Многозначный атрибут | block" loading="lazy"><br><em>Многозначный атрибут</em></p><p>Атрибут, которому может быть присвоено несколько значений.</p><p><img src="'+q+'" alt="Производный атрибут | block" loading="lazy"><br><em>Производный атрибут</em></p><p>Атрибут, чье значение можно вычислить, опираясь на значения связанных с ним атрибутов.</p><h3 id="физическая-erd" tabindex="-1"><a class="header-anchor" href="#физическая-erd"><span>Физическая ERD</span></a></h3><p>Физическая модель данных — самый детальный уровень ER-схем, где представлен процесс добавления информации в базу данных. Физические модели «сущность-связь» отображают всю структуру таблицы, включая названия столбцов, типы данных, ограничения столбцов, первичные и внешние ключи, а также отношения между таблицами.</p><p><img src="'+u+'" alt="Шаблон ER-диаграммы для библиотеки | block" loading="lazy"><br><em>Шаблон ER-диаграммы для библиотеки</em></p><p>Данный шаблон иллюстрирует обмен данными, который происходит, когда читатель берет книгу из библиотеки. Взглянув на эту диаграмму, любой участник проекта сможет с легкостью понять движение потока задействованных данных и эффективно с ним работать.</p><p>Как показано ниже, таблицы представляют собой еще один способ отображения сущностей. Вот ключевые составляющие таблиц «сущность-связь»:</p><h4 id="поля" tabindex="-1"><a class="header-anchor" href="#поля"><span>Поля</span></a></h4><p>Поля — это участки таблицы, где задаются атрибуты сущностей. Под атрибутами обычно подразумеваются столбцы базы данных, которая моделируется по принципу «сущность-связь».</p><p>В примере ниже InterestRate («процентная ставка») и LoanAmount («сумма займа») оба являются атрибутами сущности и оба находятся внутри полей.</p><p><img src="'+K+'" alt="Поля | block" loading="lazy"><br><em>Поля</em></p><h4 id="ключи" tabindex="-1"><a class="header-anchor" href="#ключи"><span>Ключи</span></a></h4><p>Ключи — один из способов категоризации атрибутов. Напоминаем, что ER-диаграммы помогают пользователям моделировать базы данных посредством таблиц, которые обеспечивают им упорядоченность, эффективность и высокую скорость работы. Ну а ключи применяются с целью максимально эффективно связать между собой разные таблицы в базе данных.</p><ul><li><p><strong>Первичные ключи</strong></p><p>Первичный ключ — это атрибут или сочетание атрибутов, идентифицирующих один конкретный экземпляр сущности.</p></li><li><p><strong>Внешние ключи</strong></p><p>Внешний ключ создается каждый раз, когда атрибут привязывается к сущности посредством единичной или множественной связи.</p></li></ul><p>К примеру, займ на каждый отдельный автомобиль может быть выдан только одним банком, поэтому в качестве внешнего ключа FinancedBy («кем выдан займ») в таблице Car (автомобиль») использован основной ключ BankId («идентификатор банка»).При этом идентификатор BankId может служить внешним ключом сразу для нескольких автомобилей.</p><p><img src="'+Q+'" alt="Ключи | block" loading="lazy"><br><em>Ключи</em></p><h4 id="типы" tabindex="-1"><a class="header-anchor" href="#типы"><span>Типы</span></a></h4><p>Под типом подразумевается тип данных в соответствующем поле таблицы. Однако это также может быть и тип сущности, то есть описание ее составляющих. Например, у сущности «книга» будут следующие типы: «автор», «название» и «дата публикации».</p><p><img src="'+C+'" alt="Типы | block" loading="lazy"><br><em>Типы</em></p><h2 id="нотация-er-диаграмм" tabindex="-1"><a class="header-anchor" href="#нотация-er-диаграмм"><span>Нотация ER-диаграмм</span></a></h2><p>Существует несколько способов описания взаимосвязей между сущностями, например нотации Бахмана, OMT, IDEF или UML, но наиболее популярной и простой считается нотация Crow&#39;s Foot («вороньи лапки»).</p><p><img src="'+J+'" alt="нотация Crow&#39;s Foot («вороньи лапки») | block" loading="lazy"><br><em>нотация Crow&#39;s Foot («вороньи лапки»)</em></p><hr><h3 id="абстрактные-типы-данных-в-er-диаграммах" tabindex="-1"><a class="header-anchor" href="#абстрактные-типы-данных-в-er-диаграммах"><span>Абстрактные типы данных в ER-диаграммах</span></a></h3><p>Во многих нотациях ER-диаграмм, особенно независимых от конкретной СУБД, используют <strong>упрощённые (абстрактные) типы данных</strong>. Это делается для того, чтобы сфокусироваться на <strong>логической структуре данных</strong>, а не на деталях реализации в той или иной СУБД (например, MySQL, PostgreSQL и др.).</p><p>Такая абстракция особенно характерна для инструментов визуализации, таких как <strong>Mermaid</strong>, <strong><a href="http://dbdiagram.io" target="_blank" rel="noopener noreferrer">dbdiagram.io</a></strong>, <strong>Lucidchart</strong> и других.</p><hr><h3 id="таблица-соответствия-абстрактные-типы-и-аналоги-в-mysql" tabindex="-1"><a class="header-anchor" href="#таблица-соответствия-абстрактные-типы-и-аналоги-в-mysql"><span>Таблица соответствия: абстрактные типы и аналоги в MySQL</span></a></h3><table><thead><tr><th>Абстрактный тип</th><th>Назначение</th><th>Примеры MySQL-типов</th></tr></thead><tbody><tr><td><code>string</code></td><td>Текстовые данные</td><td><code>VARCHAR(n)</code>, <code>CHAR(n)</code>, <code>TEXT</code>, <code>ENUM</code>, <code>SET</code></td></tr><tr><td><code>int</code></td><td>Целые числа</td><td><code>INT</code>, <code>INTEGER</code>, <code>TINYINT</code>, <code>SMALLINT</code>, <code>BIGINT</code></td></tr><tr><td><code>float</code></td><td>Числа с плавающей точкой</td><td><code>FLOAT</code>, <code>DOUBLE</code>, <code>DECIMAL(p,s)</code></td></tr><tr><td><code>boolean</code></td><td>Логическое значение</td><td><code>BOOLEAN</code>, <code>BOOL</code>, <code>TINYINT(1)</code></td></tr><tr><td><code>date</code></td><td>Только дата</td><td><code>DATE</code></td></tr><tr><td><code>time</code></td><td>Только время</td><td><code>TIME</code></td></tr><tr><td><code>datetime</code></td><td>Дата и время</td><td><code>DATETIME</code>, <code>TIMESTAMP</code></td></tr></tbody></table><blockquote><p>[!INFO]<br> Использование абстрактных типов позволяет <strong>разделить уровни логической и физической модели</strong>. На этапе проектирования ER-диаграмм часто нет необходимости указывать точные типы данных и размеры полей — это делается позже при адаптации под конкретную СУБД.</p></blockquote><h3 id="пример-схемы-с-абстрактными-типами-и-комментариями" tabindex="-1"><a class="header-anchor" href="#пример-схемы-с-абстрактными-типами-и-комментариями"><span>Пример схемы с абстрактными типами и комментариями</span></a></h3>',84)),p(n,{code:""}),t[10]||(t[10]=o('<hr><h2 id="создание-erd-при-помощи-mysql-workbench" tabindex="-1"><a class="header-anchor" href="#создание-erd-при-помощи-mysql-workbench"><span>Создание ERD при помощи MySQL Workbench</span></a></h2><p>Для создания модели БД в MySQL Workbench необходимо выполнить следующие шаги:</p><ol><li><p>Запустить MySQL Workbench и выбрать пункт меню <code>&quot;File&quot;</code> -&gt; <code>&quot;New Model&quot;</code>.</p></li><li><p>Добавить таблицы в модель, используя инструменты на панели инструментов. Для добавления таблицы необходимо перетащить элемент <code>&quot;Table&quot;</code> из панели инструментов на рабочую область модели.</p></li><li><p>Определить поля таблицы, выбрав таблицу и нажав на кнопку <code>&quot;Add Column&quot;</code> на панели инструментов. Для каждого поля необходимо указать имя, тип данных, длину и другие параметры.</p></li><li><p>Установить связи между таблицами, выбрав таблицу и нажав на кнопку <code>&quot;Add Relationship&quot;</code> на панели инструментов. Для каждой связи необходимо указать родительскую таблицу, дочернюю таблицу и поля, по которым они связаны.</p></li><li><p>Определить индексы таблицы, выбрав таблицу и нажав на кнопку <code>&quot;Add Index&quot;</code> на панели инструментов. Для каждого индекса необходимо указать поля, по которым он будет создан. (в рамках данного курса индексы мы рассмотрим позднее)</p></li><li><p>Сохранить модель в файле формата <code>.mwb</code>, используя пункт меню <code>&quot;File&quot; -&gt; &quot;Save Model&quot;</code>.</p></li><li><p>Создать базу данных на основе модели, используя пункт меню <code>&quot;Database&quot;</code> -&gt; <code>&quot;Forward Engineer...&quot;</code>. В окне настройки процесса необходимо указать параметры подключения к серверу MySQL и нажать кнопку <code>&quot;Next&quot;</code>. Затем нужно выбрать модель и нажать кнопку <code>&quot;Next&quot;</code> еще раз. В окне настройки базы данных необходимо указать имя базы данных и другие параметры, а затем нажать кнопку <code>&quot;Next&quot;</code>. Наконец, нужно нажать кнопку <code>&quot;Execute&quot;</code> для создания базы данных на основе модели.</p></li></ol><p>Таким образом, создание модели БД в MySQL Workbench не является сложной задачей, если следовать указанным выше шагам. Это позволяет создавать эффективные и удобные базы данных для различных приложений и проектов.</p>',5))])}const D=A(M,[["render",T]]),G=JSON.parse('{"path":"/disciplines/op08/op08_10.html","title":"ОП.08 - 10 - Модели БД и способы их представления. ER-диаграммы","lang":"ru-RU","frontmatter":{"description":"Примечание ЭТО АРХИВНАЯ ВЕРСИЯ КУРСА! Материалы предназначаются для пересдающих дисциплину \\"ОП.08 - Основы проектирования баз данных\\" в соответствии с учебными планами СПО годов...","head":[["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"ОП.08 - 10 - Модели БД и способы их представления. ER-диаграммы\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2025-10-31T12:29:12.000Z\\",\\"author\\":[]}"],["meta",{"property":"og:url","content":"https://it.vshp.online/disciplines/op08/op08_10.html"}],["meta",{"property":"og:site_name","content":"Кафедра ИТ"}],["meta",{"property":"og:title","content":"ОП.08 - 10 - Модели БД и способы их представления. ER-диаграммы"}],["meta",{"property":"og:description","content":"Примечание ЭТО АРХИВНАЯ ВЕРСИЯ КУРСА! Материалы предназначаются для пересдающих дисциплину \\"ОП.08 - Основы проектирования баз данных\\" в соответствии с учебными планами СПО годов..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"ru-RU"}],["meta",{"property":"og:updated_time","content":"2025-10-31T12:29:12.000Z"}],["meta",{"property":"article:modified_time","content":"2025-10-31T12:29:12.000Z"}]]},"git":{"updatedTime":1761913752000,"contributors":[{"name":"PAVEL TKACHEV","username":"","email":"phoenixweiss@ya.ru","commits":1}],"changelog":[{"hash":"a771585bc5911f4a729d06794a6706536be6cc56","time":1761913752000,"email":"phoenixweiss@ya.ru","author":"PAVEL TKACHEV","message":"Update <a href=\\"http://index.md\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">index.md</a>, abacus.png, ada_lawlace.png, and 105 more files"}]},"autoDesc":true,"filePathRelative":"disciplines/op08/op08_10.md"}');export{D as comp,G as data};
